import './App.css';
import React, { useEffect, useState } from 'react';
import Factors from './Tables/factors';
import Months from './Tables/months';
import Flights from './Tables/flights';
import AllFlights from './Tables/allFlight';
import Cancellations from './Tables/cancellations';
import Papa from 'papaparse';
import csvFile from './DataFiles/cancellations.csv';

function App() {
  const [useComp, setComp] = useState();
  var renderedComp;
  var anotherVal;
  const getCsvData = (values) => {
    anotherVal = values;
    console.log(anotherVal);
  }
  const whichComp = (vals) => {
    console.log(vals);
    if (vals === "month")
      renderedComp = <Months />;
    else if (vals === "factor")
      renderedComp = <Factors />;
    else if (vals === "allFlights")
      renderedComp = <AllFlights />;
    else if (vals === "cancel") {
      Papa.parse( csvFile, {
        header: true,
        delimiter: ',',
        download: true,
        complete: function (input) {
          getCsvData(input);
        },
        error: err => console.log("ERROR", err)
      } );
      console.log(anotherVal);
      renderedComp = <Cancellations cancelData = {anotherVal} />
    }
    else
      renderedComp = <Flights />;
    setComp(renderedComp);
  }
  const thisComp = (valuez, filez) => {
    Papa.parse( filez, {
      header: true,
      delimiter: ',',
      download: true,
      complete: function (input) {
        console.log(input);
        <Cancellations cancelData = {input} />
      },
      error: err => console.log("ERROR", err)
    } );
  }
  return (
    <div className="App">
      <nav class="navbar navbar-expand-lg navbar-light bg-primary">
        <div id ="heading">
          <h2>AirStat - Flight Status Informatics</h2>
        </div>
      </nav>
      <div class="container-fluid h-100">
        <div class="row h-100">
            <div class="col-2" id="green">
                <ul class="nav nav-pills flex-column mb-auto">
                  <li class = "nav-item">
                    <a href="#" onClick={() => whichComp("flight")}><h5>Flight Based Delays</h5></a>
                  </li>
                  <li class = "nav-item">
                    <a href="#" onClick={() => whichComp("month")}><h5>Month Based Delays</h5></a>
                  </li>
                  <li class = "nav-item">
                    <a href="#"onClick={() => whichComp("factor")}><h5>Factors Affecting Delays</h5></a>
                  </li>
                  <li class = "nav-item">
                    <a href="#"onClick={() => whichComp("allFlights")}><h5>All Current Flights</h5></a>
                  </li>
                  <li class = "nav-item">
                    <a href="#"onClick={() => thisComp("cancel",csvFile)}><h5>Flight Cancellations</h5></a>
                  </li>
                </ul>
            </div>
            <div class="col-10">
              {useComp}
            </div>
        </div>
      </div>
    </div>
  );
}

export default App;
