import './App.css';
import React, { useEffect, useState } from 'react';
import Factors from './Tables/factors';
import AllFlights from './Tables/allFlight';
import Cancellations from './Tables/cancellations';
import Papa from 'papaparse';
import cancel from './DataFiles/cancellations.csv';
import large from './DataFiles/largedelays.csv';
import moderate from './DataFiles/largedelays.csv';
import small from './DataFiles/smalldelays.csv';

function App() {
  const [useComp, setComp] = useState();
  var renderedComp;

  const thisComp = (comp) => {
    Papa.parse( comp, {
      header: true,
      delimiter: ',',
      download: true,
      complete: function (input) {
        console.log(input);
        renderedComp = <Cancellations cancelData = {input.data} />
        setComp(renderedComp);
      },
      error: err => console.log("ERROR", err)
    } );
  }
  return (
    <div className="App">
      <nav class="navbar navbar-expand-lg navbar-light bg-primary">
        <div id ="heading">
          <h2>AirStat - Flight Status Informatics</h2>
        </div>
      </nav>
      <div class="container-fluid h-100">
        <div class="row h-100">
            <div class="col-2" id="green">
                <ul class="nav nav-pills flex-column mb-auto">
                  <li class = "nav-item">
                    <a href="#" onClick={() => whichComp("flight")}><h5>Flight Based Delays</h5></a>
                  </li>
                  <li class = "nav-item">
                    <a href="#"onClick={() => whichComp("allFlights")}><h5>All Current Flights</h5></a>
                  </li>
                  <li class = "nav-item">
                    <a href="#"onClick={() => thisComp("cancel")}><h5>Flight Cancellations</h5></a>
                  </li>
                </ul>
            </div>
            <div class="col-10">
              {useComp}
            </div>
        </div>
      </div>
    </div>
  );
}

export default App;
